def sum_ab(a, b):
    return a + b

def subs_ab(a, b):
    return a - b

def mult_ab(a, b):
    return a * b

def div_ab_float(a, b):
    return a / b

def div_ab_int(a, b):
    return a // b

def modulo_ab(a, b):
    return a % b

def numberiseven(a):
    if(a % 2 == 0):
        return True
    else:
        return False