    DescriptionFormatted: Final[str] = "desf"
    DescriptionId: Final[str] = "desid"
    DescriptionPlain: Final[str] = "desp"

    List: Final[str] = "lt"
    Login: Final[str] = "lgn"

    Name: Final[str] = "na"

    Password: Final[str] = "pw"

    Result: Final[str] = "ret"

    Signup: Final[str] = "sgu"
